mapper.select:
SELECT SYS_CD , MOD_ID , MOD_NM , MOD_TY , MOD_GCD , CLA_NM , USE_YN , DES2 , DUT_L , DUT_M , DUT_S , INQ_YN , RG_YN , DEL_YN , PRT_YN , EXL_YN , CON_YN , ERNAM , ERNAMT , ERDAT , AENAM , AENAMT , AEDAT , MESSAGE FROM NEXA_SAMPLE WHERE MOD_ID LIKE #{modId} || '%' AND MOD_NM LIKE '%' || #{modNm} || '%'
AND MOD_GCD = #{modGcd}
AND MOD_TY = #{modTy}
AND SYS_CD = #{sysCd}

mapper.insert:
    	INSERT 
    	  INTO NEXA_SAMPLE (
    	  	SYS_CD
			 , MOD_ID
			 , MOD_NM
			 , MOD_TY
			 , MOD_GCD
			 , CLA_NM
			 , USE_YN
			 , DES2
			 , DUT_L
			 , DUT_M
			 , DUT_S
			 , INQ_YN
			 , RG_YN
			 , DEL_YN
			 , PRT_YN
			 , EXL_YN
			 , CON_YN
			 , ERNAM
			 , ERNAMT
			 , ERDAT
			 , AENAM
			 , AENAMT
			 , AEDAT
			 , MESSAGE
		) VALUES (
			#{sysCd}
			 , #{modId}
			 , #{modNm}
			 , #{modTy}
			 , #{modGcd}
			 , #{claNm}
			 , #{useYn}
			 , #{des2}
			 , #{dutL}
			 , #{dutM}
			 , #{dutS}
			 , #{inqYn}
			 , #{rgYn}
			 , #{delYn}
			 , #{prtYn}
			 , #{exlYn}
			 , #{conYn}
			 , #{ernam}
			 , #{ernamt}
			 , #{erdat}
			 , #{aenam}
			 , #{aenamt}
			 , #{aedat}
			 , #{message}
		)

mapper.update:
        UPDATE NEXA_SAMPLE
           SET SYS_CD = #{sysCd}
			 , MOD_NM = #{modNm}
			 , MOD_TY = #{modTy}
			 , MOD_GCD = #{modGcd}
			 , CLA_NM = #{claNm}
			 , USE_YN = #{useYn}
			 , DES2 = #{des2}
			 , DUT_L = #{dutL}
			 , DUT_M = #{dutM}
			 , DUT_S = #{dutS}
			 , INQ_YN = #{inqYn}
			 , RG_YN = #{rgYn}
			 , DEL_YN = #{delYn}
			 , PRT_YN = #{prtYn}
			 , EXL_YN = #{exlYn}
			 , CON_YN = #{conYn}
			 , ERNAM = #{ernam}
			 , ERNAMT = #{ernamt}
			 , ERDAT = #{erdat}
			 , AENAM = #{aenam}
			 , AENAMT = #{aenamt}
			 , AEDAT = #{aedat}
			 , MESSAGE = #{message}
         WHERE MOD_ID = #{modId}
