mapper.insert:
    	INSERT INTO SAMPLE (
    		SAMPLE_ID
			, SAMPLE_NAME
			, SAMPLE_TYPE
			, LOC

    	) VALUES (
    		#{sampleId}
    		, #{sampleName}
    		, #{sampleType}
    		, #{loc}

    	)

mapper.update:
UPDATE SAMPLE
   SET SAMPLE_NAME = #{sampleName}
     , SAMPLE_TYPE = #{sampleType}
     , LOC = #{loc}

 WHERE SAMPLE_ID = #{sampleId}
